import React, { Component } from 'react';
import CardList from './CardList';
import SearchBox from './SearchBox';
import { robots } from './robots';


class App extends Component {
	constructor() {
		super()
		this.state = { // something that can change and affect our app
			robots: robots,
			searchfield: ''
		}
	}
	onSearchChange = (event) => { //anytime you make your own methods on a component
		this.setState({ searchfield: event.target.value})
	}

	render () {
		const filteredRobots = this.state.robots.filter(robot => {
			return robot.name.toLowerCase().includes(this.state.searchfield.toLowerCase());
		})
		return ( //remember we only need to return just one single parent so wrap around a div
			<div className = 'tc'>
				<h1> RoboFriends</h1>
				<SearchBox searchChange = {this.onSearchChange}/>
				<CardList robots = {filteredRobots}/>
			</div>
		);
	}
}

export default App;
