import React, { Component } from 'react';
import CardList from '../components/CardList';
import SearchBox from '../components/SearchBox';
import Scroll from '../components/Scroll';
import './App.css';


class App extends Component {
	constructor() {
		super()
		this.state = { // something that can change and affect our app
			robots: [],
			searchfield: ''
		}
	}

	componentDidMount() {
		fetch('https://jsonplaceholder.typicode.com/users')
		.then(response => response.json())
		.then(users => this.setState({ robots: users}));
	}
	onSearchChange = (event) => { //anytime you make your own methods on a component
		this.setState({ searchfield: event.target.value})
	}

	render () {
		const { robots, searchfield } = this.state; //destrucuting
		const filteredRobots = robots.filter(robot => {
			return robot.name.toLowerCase().includes(searchfield.toLowerCase());
		})

		if (robots.length === 0) {
			return <h1>Loading</h1>
		}
		else {
			return ( //remember we only need to return just one single parent so wrap around a div
				<div className = 'tc'>
					<h1 className= 'f1'> RoboFriends</h1>
					<SearchBox searchChange = {this.onSearchChange}/>
					<Scroll>
						<CardList robots = {filteredRobots}/>
					</Scroll>
				</div>
			);
		}
	}
}

export default App;
